  `NOISE` DOUBLE DEFAULT 0 NOT NULL,
  `SNR` DOUBLE DEFAULT 0 NOT NULL,
  `CREATE_TIME` TIMESTAMP DEFAULT CURRENT_TIMESTAMP NULL,
  `CREATE_BY` VARCHAR(100) DEFAULT 'SYS',
  `UPDATE_TIME` TIMESTAMP DEFAULT CURRENT_TIMESTAMP NULL,
  `UPDATE_BY` VARCHAR(100) DEFAULT 'SYS',
  PRIMARY KEY (`DATA_ID`),
  CONSTRAINT UK UNIQUE (`POLLING_TIME`, `CLIENT_MAC`),
  INDEX IDX1 (`POLLING_TIME`),
  INDEX IDX2 (`CLIENT_MAC`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE=utf8_general_ci;

