  `ID` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,
  `DEV_NAME` varchar(50) NOT NULL,
  `DATE` date NOT NULL,
  `TIME` time NOT NULL,
  `SRC_IP` varchar(65) NOT NULL,
