  `CREATE_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `CREATE_BY` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'SYS',
  `UPDATE_TIME` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `UPDATE_BY` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT 'SYS',
  PRIMARY KEY (`ID`,`DATE`),
  KEY `IDX1_1` (`DATE`,`TIME`),
  KEY `IDX2_1` (`DEV_NAME`,`DATE`,`TIME`),
  KEY `IDX2_2` (`SRC_IP`,`DATE`,`TIME`),
  KEY `IDX2_3` (`SRC_PORT`,`DATE`,`TIME`),
  KEY `IDX2_4` (`DST_IP`,`DATE`,`TIME`),
