  KEY `IDX3_7` (`DEV_NAME`,`DST_IP`,`DATE`,`TIME`),
  KEY `IDX3_8` (`DEV_NAME`,`DST_PORT`,`DATE`,`TIME`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT
PARTITION BY RANGE (to_days(`DATE`))
(
